        [Fact]
        public async Task WriteStreamTest()
        {
            // setup
            var mockHttp = GetMockHttpMessageHandler();
            
            Server server = new Server
            {
                Services = {Publisher.BindService(new PluginRJGWebsite.Plugin.Plugin(mockHttp.ToHttpClient()))},
                Ports = {new ServerPort("localhost", 0, ServerCredentials.Insecure)}
            };
            server.Start();

            var port = server.Ports.First().BoundPort;

            var channel = new Channel($"localhost:{port}", ChannelCredentials.Insecure);
            var client = new Publisher.PublisherClient(channel);

            var connectRequest = GetConnectSettings();

            var prepareRequest = new PrepareWriteRequest()
            {
                Schema = new Schema
                {
                    Id = "Classes",
                    Properties =
                    {
                        new Property
                        {
                            Id = "date_modified",
                            IsUpdateCounter = true
                        }
                    }
                },
                CommitSlaSeconds = 1
            };
            
            var records = new List<Record>()
            {
                {new Record
                {
                    Action = Record.Types.Action.Upsert,
                    CorrelationId = "test",
                    DataJson = "{\"id\":58134,\"sku\":\"\",\"course_id\":50382,\"course_sku\":\"\",\"start_date\":\"2019/08/07\",\"end_date\":\"2019/11/07\",\"location\":\"\",\"language\":\"English\",\"visible\":true,\"open_seats\":12,\"price\":\"1025.00\",\"currency\":\"USD\",\"date_created\":\"2019-03-18T14:16:36\",\"date_created_gmt\":\"2019-03-18T18:16:36\",\"date_modified\":\"2019-03-22T11:05:04\",\"date_modified_gmt\":\"2019-03-19T15:05:04\",\"_links\":{\"self\":[{\"href\":\"https://rjgdev.clockwork360.com/wp-json/rjg/v1/classes/58134\"}],\"collection\":[{\"href\":\"https://rjgdev.clockwork360.com/wp-json/rjg/v1/classes\"}],\"rjg:course\":[{\"href\":\"https://rjgdev.clockwork360.com/wp-json/rjg/v1/courses/\"}],\"wc:object\":[{\"href\":\"https://rjgdev.clockwork360.com/wp-json/wc/v3/products/50382/variations/58134\"}]}}"
                }}
            };
            
            var recordAcks = new List<RecordAck>();

            // act
            client.Connect(connectRequest);
            client.PrepareWrite(prepareRequest);
            
            using (var call = client.WriteStream())
            {
                var responseReaderTask = Task.Run(async () =>
                {
                    while (await call.ResponseStream.MoveNext())
                    {
                        var ack = call.ResponseStream.Current;
                        recordAcks.Add(ack);
                    }
                });

                foreach (Record record in records)
                {
                    await call.RequestStream.WriteAsync(record);
                }
                await call.RequestStream.CompleteAsync();
                await responseReaderTask;
            }

            // assert
            Assert.Single(recordAcks);
            Assert.Equal("",recordAcks[0].Error);
            Assert.Equal("test",recordAcks[0].CorrelationId);

            // cleanup
            await channel.ShutdownAsync();
            await server.ShutdownAsync();
        }

